전 그냥 날림식 코딩이라 이런식이네요.

if (msg.indexOf ("!날씨 ") == 0) {
var where = msg.substr (4);
try{
var link = Utils.getWebText("https://m.search.naver.com/search.naver?query=" + where + "+날씨");
var clin = link
var hour_rain = link.split ("<div class=\"status_list rain\">")[1].split (">")[11].split ("<")[0]; //1시간 강수량
var hr = "1시간 강수량: " + hour_rain + "mm\n";
} catch(e) {
var hr =""
}
 
try{
var link = Utils.getWebText("https://m.search.naver.com/search.naver?query=" + where + "+날씨");
var day_rain = link.split ("<div class=\"status_list rain\">")[1].split (">")[19].split ("<")[0]; //일간 강수량
var dr = "일일 강수량: " + day_rain + "mm\n";
} catch(e) {
var dr=""
}
 
try{
var link = Utils.getWebText("https://m.search.naver.com/search.naver?query=" + where + "+날씨");
var mo = link.split ("<div class=\"api_more_wrap\">")[1].split ('"')[3].split ('"')[0]; //상세 기상정보 가져오기
var mlink = Utils.getWebText(mo);
var dust = mlink.split ("<li class=\"finedust\">")[1].split(">")[2].split("<")[0];
var mdust = mlink.split ("<li class=\"finedust\">")[1].split(">")[6].split("<")[0];
 
var stat = mlink.split ("<li class=\"finedust\">")[1].split(">")[1].split("<")[0];
var mstat = mlink.split ("<li class=\"finedust\">")[1].split(">")[5].split("<")[0];
var du = "미세먼지: " + dust + "μg/m³"+" ("+stat+")\n초미세먼지: "+mdust + "μg/m³"+" ("+mstat+")\n"
} catch(e) {
var du="미세먼지: Error\n"
}
 
try {
var loc= "";
 
var link = Utils.getWebText("https://m.search.naver.com/search.naver?query=" + where + "+날씨");
var mo = link.split ("<div class=\"api_more_wrap\">")[1].split ('"')[3].split ('"')[0]; //상세 기상정보 가져오기
var mlink = Utils.getWebText(mo);
 
var locate = mlink.split ("<div class=\"section_location\">")[1].split (">")[8].split ("<")[0]; //검색 위치
var now = link.split ("<div class=\"wt_summary\">")[1].split (">")[1].split (",")[0].replace('구름','구\ufeff름'); //현재 날씨
var temp = link.split ("<em class=\"figure degree_code\">")[1].split ("<")[0]; //온도
var mtemp = link.split ("<em class=\"figure degree_code\">")[2].split ("<")[0]; //체감온도
var min = link.split ("<div class=\"wt_temp\">")[1].split (">")[4].split ("<")[0]; //최저기온
var max = link.split ("<div class=\"wt_temp\">")[1].split (">")[12].split ("<")[0]; //최고기온
var metp = max - min;
var wind = link.split ("<span class=\"metersec\">")[1].split (">")[0].split ("<")[0]; //풍속
 
//var water = mlink.split ("<span class=\"percent\">")[1].split (">")[0].split ("<")[0];// 습도
var am_rain = link.split ("<li class=\"wt_item today\">")[1].split ("<div")[2].split (">")[8].split ("<")[0]; //오전 강수확률
var pm_rain = link.split ("<li class=\"wt_item today\">")[1].split ("<div")[2].split (">")[20].split ("<")[0]; //오후 강수확률
 
var sun = mlink.split ("<span class=\"sunrise\">")[1].split (">")[0].split ("<")[0];// 일출
var moon = mlink.split ("<span class=\"sunset\">")[1].split (">")[0].split ("<")[0];// 일몰
 
//내일 날씨
var tmr_min = link.split ("<div class=\"wt_temp\">")[2].split (">")[4].split ("<")[0]; //내일 최저기온
var tmr_max = link.split ("<div class=\"wt_temp\">")[2].split (">")[12].split ("<")[0]; //내일 최저기온
var tmr_am = link.split ("<div class=\"wt_cast\">")[2].split (">")[5].split ("<")[0]; //오전 강수확률
var tmr_pm = link.split ("<div class=\"wt_cast\">")[2].split (">")[15].split ("<")[0]; //오전 강수확률
 
var lm = mlink.split ("<div class=\"layer_info\">")[1].split (">")[3].split ("<")[0];
var uv = mlink.split ("<div class=\"layer_info\">")[1].split (">")[4].split ("<")[0];
 
 
if (am_rain == 0) { var am_rr = "" ;} else { var am_rr = "오전 강수확률: " + am_rain +"%\n"};
if (pm_rain == 0) { var pm_rr = "" ;} else { var pm_rr = "오후 강수확률: " + pm_rain +"%\n"};
 
if (tmr_am == 0) { var tma_rr = "" ;} else { var tma_rr = "\n오전 강수확률: " + tmr_am +"%"};
if (tmr_pm == 0) { var tmp_rr = "" ;} else { var tmp_rr = "\n오후 강수확률: " + tmr_pm +"%"};
 
 
 
replier.reply ("현재 날씨: "+now+ "\n\n["+where+"]의 날씨 정보\n위치: "+locate+"\n현재온도: "+temp+"℃\n체감온도: "+mtemp+"℃\n최저기온: "
+min+"℃\n최고기온: "+max+"℃\n일교차: " + metp +"℃\n풍속: "+ wind + "m/s\n자외선: "
+lm+uv+"\n"+du+am_rr+pm_rr+hr+dr+"일출/일몰: "+sun+" / "+moon+"\n\n["+where+
" 내일 날씨]\n최저 기온: "+tmr_min+"℃\n최고 기온: "
+tmr_max+"℃"+tma_rr+tmp_rr); //값 출력 
 
 
} catch (e) {
 
replier.reply ("검색 결과가 없습니다.");
 
}
 
}
